                new JmVerse {Month=11, Reference="1 Timothy 4:7-8 (NIV)", Text="Have nothing to do with godless myths and old wives' tales; rather, train yourself to be godly. For physical training is of some value, but godliness has value for all things, holding promise for both the present life and the life to come.", Year="BOOKS" , StartDate=DateTime.Parse("2017-11-15")},
