            var wmCatechisms = new List<WestminsterCatechism>
            {
                new WestminsterCatechism {Number=58, Question="What is required in the fourth commandment?", Answer="The fourth commandment requireth the keeping holy to God such set times as he hath appointed in his word; expressly one whole day in seven, to be a holy sabbath to himself." },
                new WestminsterCatechism {Number=34, Question="What is adoption?", Answer="Adoption is an act of God's free grace, whereby we are received into the number, and have a right to all the privileges of, the sons of God." },
                new WestminsterCatechism {Number=12, Question="What special act of providence did God exercise toward man in the estate wherein he was created?", Answer="When God had created man, he entered into a covenant of life with him, upon condition of perfect obedience; forbidding him to eat of the tree of the knowledge of good and evil, upon the pain of death." },
                new WestminsterCatechism {Number=15, Question="What was the sin whereby our first parents fell from the estate wherein they were created?", Answer="The sin whereby our first parents fell from the estate wherein they were created was their eating the forbidden fruit." },
                new WestminsterCatechism {Number=44, Question="What doth the preface to the ten commandments teach us?", Answer="The preface to the ten commandments teacheth us that because God is the Lord, and our God, and redeemer, therefore we are bound to keep all his commandments." },
                new WestminsterCatechism {Number=62, Question="What are the reasons annexed to the fourth commandment?", Answer="The reasons annexed to the fourth commandment are, God's allowing us six days of the week for our own employments, his challenging a special propriety in the seventh, his own example, and his blessing the sabbath day." },
                new WestminsterCatechism {Number=21, Question="Who is the redeemer of God's elect?", Answer="The only redeemer of God's elect is the Lord Jesus Christ, who, being the eternal Son of God, became man, and so was, and continueth to be, God and man in two distinct natures, and one person, forever." },
                new WestminsterCatechism {Number=60, Question="How is the sabbath to be sanctified?", Answer="The sabbath is to be sanctified by a holy resting all that day, even from such worldly employments and recreations as are lawful on other days; and spending the whole time in the public and private exercises of God's worship, except so much as is to be taken up in the works of necessity and mercy." },
                new WestminsterCatechism {Number=40, Question="What did God at first reveal to man for the rule of his obedience?", Answer="The rule which God at first revealed to man for his obedience was the moral law." },
                new WestminsterCatechism {Number=14, Question="What is sin?", Answer="Sin is any want of conformity unto, or transgression of, the law of God." },
                new WestminsterCatechism {Number=92, Question="What is a sacrament?", Answer="A sacrament is an holy ordinance instituted by Christ; wherein, by sensible signs, Christ, and the benefits of the new covenant, are represented, sealed, and applied to believers." },
                new WestminsterCatechism {Number=9, Question="What is the work of creation?", Answer="The work of creation is God's making all things of nothing, by the word of his power, in the space of six days, and all very good." },
                new WestminsterCatechism {Number=107, Question="What doth the conclusion of the Lord's prayer teach us?", Answer="The conclusion of the Lord's prayer, which is, For thine is the kingdom, and the power, and the glory, forever, Amen, teacheth us to take our encouragement in prayer from God only, and in our prayers to praise him, ascribing kingdom, power and glory to him. And in testimony of our desire, and assurance to be heard, we say, Amen." },
                new WestminsterCatechism {Number=52, Question="What are the reasons annexed to the second commandment?", Answer="The reasons annexed to the second commandment are, God's sovereignty over us, his propriety in us, and the zeal he hath to his own worship." },
                new WestminsterCatechism {Number=45, Question="Which is the first commandment?", Answer="The first commandment is, Thou shalt have no other gods before me." },
                new WestminsterCatechism {Number=84, Question="What doth every sin deserve?", Answer="Every sin deserveth God's wrath and curse, both in this life, and that which is to come." },
                new WestminsterCatechism {Number=53, Question="Which is the third commandment?", Answer="The third commandment is, Thou shalt not take the name of the Lord thy God in vain: for the Lord will not hold him guiltless that taketh his name in vain." },
                new WestminsterCatechism {Number=98, Question="What is prayer?", Answer="Prayer is an offering up of our desires unto God, for things agreeable to his will, in the name of Christ, with confession of our sins, and thankful acknowledgment of his mercies." },
                new WestminsterCatechism {Number=61, Question="What is forbidden in the fourth commandment?", Answer="The fourth commandment forbiddeth the omission or careless performance of the duties required, and the profaning the day by idleness, or doing that which is in itself sinful, or by unnecessary thoughts, words or works, about our worldly employments or recreations." },
                new WestminsterCatechism {Number=69, Question="What is forbidden in the sixth commandment?", Answer="The sixth commandment forbiddeth the taking away of our own life, or the life of our neighbor unjustly, or whatsoever tendeth thereunto." },
                new WestminsterCatechism {Number=86, Question="What is faith in Jesus Christ?", Answer="Faith in Jesus Christ is a saving grace, whereby we receive and rest upon him alone for salvation, as he is offered to us in the gospel." },
                new WestminsterCatechism {Number=6, Question="How many persons are there in the godhead?", Answer="There are three persons in the Godhead; the Father, the Son, and the Holy Ghost; and these three are one God, the same in substance, equal in power and glory." },
                new WestminsterCatechism {Number=71, Question="What is required in the seventh commandment?", Answer="The seventh commandment requireth the preservation of our own and our neighbor's chastity, in heart, speech and behavior." },
                new WestminsterCatechism {Number=2, Question="What rule hath God given to direct us how we may glorify and enjoy him?", Answer="The word of God, which is contained in the scriptures of the Old and New Testaments, is the only rule to direct us how we may glorify and enjoy him." },
                new WestminsterCatechism {Number=91, Question="How do the sacraments become effectual means of salvation?", Answer="The sacraments become effectual means of salvation, not from any virtue in them, or in him that doth administer them; but only by the blessing of Christ, and the working of his Spirit in them that by faith receive them." },
                new WestminsterCatechism {Number=17, Question="Into what estate did the fall bring mankind?", Answer="The fall brought mankind into an estate of sin and misery." },
                new WestminsterCatechism {Number=49, Question="Which is the second commandment?", Answer="The second commandment is, Thou shalt not make unto thee any graven image, or any likeness of anything that is in heaven above, or that is in the earth beneath, or that is in the water under the earth: thou shalt not bow down thyself to them, nor serve them: for I the Lord thy God am a jealous God, visiting the iniquity of the fathers upon the children unto the third and fourth generation of them that hate me; and showing mercy unto thousands of them that love me, and keep my commandments." },
                new WestminsterCatechism {Number=66, Question="What is the reason annexed to the fifth commandment?", Answer="The reason annexed to the fifth commandment is a promise of long life and prosperity (as far as it shall serve for God's glory and their own good) to all such as keep this commandment." },
                new WestminsterCatechism {Number=4, Question="What is God?", Answer="God is a spirit, infinite, eternal, and unchangeable, in his being, wisdom, power, holiness, justice, goodness and truth." },
                new WestminsterCatechism {Number=74, Question="What is required in the eighth commandment?", Answer="The eighth commandment requireth the lawful procuring and furthering the wealth and outward estate of ourselves and others." },
                new WestminsterCatechism {Number=16, Question="Did all mankind fall in Adam's first transgression?", Answer="The covenant being made with Adam, not only for himself, but for his posterity; all mankind, descending from him by ordinary generation, sinned in him, and fell with him, in his first transgression." },
                new WestminsterCatechism {Number=88, Question="What are the outward means whereby Christ communicateth to us the benefits of redemption?", Answer="The outward and ordinary means whereby Christ communicateth to us the benefits of redemption, are his ordinances, especially the word, sacraments, and prayer; all which are made effectual to the elect for salvation." },
                new WestminsterCatechism {Number=48, Question="What are we specially taught by these words before me in the first commandment?", Answer="These words before me in the first commandment teach us that God, who seeth all things, taketh notice of, and is much displeased with, the sin of having any other god." },
                new WestminsterCatechism {Number=56, Question="What is the reason annexed to the third commandment?", Answer="The reason annexed to the third commandment is that however the breakers of this commandment may escape punishment from men, yet the Lord our God will not suffer them to escape his righteous judgment." },
                new WestminsterCatechism {Number=42, Question="What is the sum of the ten commandments?", Answer="The sum of the ten commandments is to love the Lord our God with all our heart, with all our soul, with all our strength, and with all our mind; and our neighbor as ourselves." },
                new WestminsterCatechism {Number=43, Question="What is the preface to the ten commandments?", Answer="The preface to the ten commandments is in these words, I am the Lord thy God, which have brought thee out of the land of Egypt, out of the house of bondage." },
                new WestminsterCatechism {Number=79, Question="Which is the tenth commandment?", Answer="The tenth commandment is, Thou shalt not covet thy neighbor's house, thou shalt not covet thy neighbor's wife, nor his manservant, nor his maidservant, nor his ox, nor his ass, nor anything that is thy neighbor's." },
                new WestminsterCatechism {Number=65, Question="What is forbidden in the fifth commandment?", Answer="The fifth commandment forbiddeth the neglecting of, or doing anything against, the honor and duty which belongeth to every one in their several places and relations." },
                new WestminsterCatechism {Number=51, Question="What is forbidden in the second commandment?", Answer="The second commandment forbiddeth the worshiping of God by images, or any other way not appointed in his word." },
                new WestminsterCatechism {Number=70, Question="Which is the seventh commandment?", Answer="The seventh commandment is, Thou shalt not commit adultery." },
                new WestminsterCatechism {Number=18, Question="Wherein consists the sinfulness of that estate whereinto man fell?", Answer="The sinfulness of that estate whereinto man fell consists in the guilt of Adam's first sin, the want of original righteousness, and the corruption of his whole nature, which is commonly called original sin; together with all actual transgressions which proceed from it." },
                new WestminsterCatechism {Number=85, Question="What doth God require of us that we may escape his wrath and curse due to us for sin?", Answer="To escape the wrath and curse of God due to us for sin, God requireth of us faith in Jesus Christ, repentance unto life, with the diligent use of all the outward means whereby Christ communicateth to us the benefits of redemption." },
                new WestminsterCatechism {Number=23, Question="What offices doth Christ execute as our redeemer?", Answer="Christ, as our redeemer, executeth the offices of a prophet, of a priest, and of a king, both in his estate of humiliation and exaltation." },
                new WestminsterCatechism {Number=37, Question="What benefits do believers receive from Christ at death?", Answer="The souls of believers are at their death made perfect in holiness, and do immediately pass into glory; and their bodies, being still united to Christ, do rest in their graves till the resurrection." },
                new WestminsterCatechism {Number=13, Question="Did our first parents continue in the estate wherein they were created?", Answer="Our first parents, being left to the freedom of their own will, fell from the estate wherein they were created, by sinning against God." },
                new WestminsterCatechism {Number=28, Question="Wherein consisteth Christ's exaltation?", Answer="Christ's exaltation consisteth in his rising again from the dead on the third day, in ascending up into heaven, in sitting at the right hand of God the Father, and in coming to judge the world at the last day." },
                new WestminsterCatechism {Number=26, Question="How doth Christ execute the office of a king?", Answer="Christ executeth the office of a king, in subduing us to himself, in ruling and defending us, and in restraining and conquering all his and our enemies." },
                new WestminsterCatechism {Number=32, Question="What benefits do they that are effectually called partake of in this life?", Answer="They that are effectually called do in this life partake of justification, adoption and sanctification, and the several benefits which in this life do either accompany or flow from them." },
                new WestminsterCatechism {Number=47, Question="What is forbidden in the first commandment?", Answer="The first commandment forbiddeth the denying, or not worshiping and glorifying the true God as God, and our God; and the giving of that worship and glory to any other, which is due to him alone." },
                new WestminsterCatechism {Number=46, Question="What is required in the first commandment?", Answer="The first commandment requireth us to know and acknowledge God to be the only true God, and our God; and to worship and glorify him accordingly." },
                new WestminsterCatechism {Number=22, Question="How did Christ, being the Son of God, become man?", Answer="Christ, the Son of God, became man, by taking to himself a true body and a reasonable soul, being conceived by the power of the Holy Ghost in the womb of the virgin Mary, and born of her, yet without sin." },
                new WestminsterCatechism {Number=100, Question="What doth the preface of the Lord's prayer teach us?", Answer="The preface of the Lord's prayer, which is, Our Father which art in heaven, teacheth us to draw near to God with all holy reverence and confidence, as children to a father able and ready to help us; and that we should pray with and for others." },
                new WestminsterCatechism {Number=68, Question="What is required in the sixth commandment?", Answer="The sixth commandment requireth all lawful endeavors to preserve our own life, and the life of others." },
                new WestminsterCatechism {Number=78, Question="What is forbidden in the ninth commandment?", Answer="The ninth commandment forbiddeth whatsoever is prejudicial to truth, or injurious to our own or our neighbor's good name." },
                new WestminsterCatechism {Number=1, Question="What is the chief end of man?", Answer="To glorify God and enjoy Him forever" },
                new WestminsterCatechism {Number=75, Question="What is forbidden in the eighth commandment?", Answer="The eighth commandment forbiddeth whatsoever doth or may unjustly hinder our own or our neighbor's wealth or outward estate." },
                new WestminsterCatechism {Number=63, Question="Which is the fifth commandment?", Answer="The fifth commandment is, Honor thy father and thy mother; that thy days may be long upon the land which the Lord thy God giveth thee." },
                new WestminsterCatechism {Number=19, Question="What is the misery of that estate whereinto man fell?", Answer="All mankind by their fall lost communion with God, are under his wrath and curse, and so made liable to all miseries in this life, to death itself, and to the pains of hell forever." },
                new WestminsterCatechism {Number=80, Question="What is required in the tenth commandment?", Answer="The tenth commandment requireth full contentment with our own condition, with a right and charitable frame of spirit toward our neighbor, and all that is his." },
                new WestminsterCatechism {Number=96, Question="What is the Lord's supper?", Answer="The Lord's supper is a sacrament, wherein, by giving and receiving bread and wine according to Christ's appointment, his death is showed forth; and the worthy receivers are, not after a corporal and carnal manner, but by faith, made partakers of his body and blood, with all his benefits, to their spiritual nourishment and growth in grace." },
                new WestminsterCatechism {Number=10, Question="How did God create man?", Answer="God created man male and female, after his own image, in knowledge, righteousness and holiness, with dominion over the creatures." },
                new WestminsterCatechism {Number=104, Question="What do we pray for in the fourth petition?", Answer="In the fourth petition, which is, Give us this day our daily bread, we pray that of God's free gift we may receive a competent portion of the good things of this life, and enjoy his blessing with them." },
                new WestminsterCatechism {Number=5, Question="Are there more Gods than one?", Answer="There is but one only, the living and true God." },
                new WestminsterCatechism {Number=95, Question="To whom is baptism to be administered?", Answer="Baptism is not to be administered to any that are out of the visible church, till they profess their faith in Christ, and obedience to him; but the infants of such as are members of the visible church are to be baptized." },
                new WestminsterCatechism {Number=72, Question="What is forbidden in the seventh commandment?", Answer="The seventh commandment forbiddeth all unchaste thoughts, words and actions." },
                new WestminsterCatechism {Number=99, Question="What rule hath God given for our direction in prayer?", Answer="The whole word of God is of use to direct us in prayer; but the special rule of direction is that form of prayer which Christ taught his disciples, commonly called the Lord's prayer." },
                new WestminsterCatechism {Number=24, Question="How doth Christ execute the office of a prophet?", Answer="Christ executeth the office of a prophet, in revealing to us, by his word and Spirit, the will of God for our salvation." },
                new WestminsterCatechism {Number=36, Question="What are the benefits which in this life do accompany or flow from justification, adoption and sanctification?", Answer="The benefits which in this life do accompany or flow from justification, adoption and sanctification, are, assurance of God's love, peace of conscience, joy in the Holy Ghost, increase of grace, and perseverance therein to the end." },
                new WestminsterCatechism {Number=82, Question="Is any man able perfectly to keep the commandments of God?", Answer="No mere man since the fall is able in this life perfectly to keep the commandments of God, but doth daily break them in thought, word and deed." },
                new WestminsterCatechism {Number=41, Question="Where is the moral law summarily comprehended?", Answer="The moral law is summarily comprehended in the ten commandments." },
                new WestminsterCatechism {Number=101, Question="What do we pray for in the first petition?", Answer="In the first petition, which is, Hallowed be thy name, we pray that God would enable us and others to glorify him in all that whereby he maketh himself known; and that he would dispose all things to his own glory." },
                new WestminsterCatechism {Number=76, Question="Which is the ninth commandment?", Answer="The ninth commandment is, Thou shalt not bear false witness against thy neighbor." },
                new WestminsterCatechism {Number=105, Question="What do we pray for in the fifth petition?", Answer="In the fifth petition, which is, And forgive us our debts, as we forgive our debtors, we pray that God, for Christ's sake, would freely pardon all our sins; which we are the rather encouraged to ask, because by his grace we are enabled from the heart to forgive others." },
                new WestminsterCatechism {Number=35, Question="What is sanctification?", Answer="Sanctification is the work of God's free grace, whereby we are renewed in the whole man after the image of God, and are enabled more and more to die unto sin, and live unto righteousness." },
                new WestminsterCatechism {Number=54, Question="What is required in the third commandment?", Answer="The third commandment requireth the holy and reverent use of God's names, titles, attributes, ordinances, word and works." },
                new WestminsterCatechism {Number=50, Question="What is required in the second commandment?", Answer="The second commandment requireth the receiving, observing, and keeping pure and entire, all such religious worship and ordinances as God hath appointed in his word." },
                new WestminsterCatechism {Number=20, Question="Did God leave all mankind to perish in the estate of sin and misery?", Answer="God having, out of his mere good pleasure, from all eternity, elected some to everlasting life, did enter into a covenant of grace, to deliver them out of the estate of sin and misery, and to bring them into an estate of salvation by a redeemer." },
                new WestminsterCatechism {Number=87, Question="What is repentance unto life?", Answer="Repentance unto life is a saving grace, whereby a sinner, out of a true sense of his sin, and apprehension of the mercy of God in Christ, doth, with grief and hatred of his sin, turn from it unto God, with full purpose of, and endeavor after, new obedience." },
                new WestminsterCatechism {Number=93, Question="Which are the sacraments of the New Testament?", Answer="The sacraments of the New Testament are baptism and the Lord's supper." },
                new WestminsterCatechism {Number=67, Question="Which is the sixth commandment?", Answer="The sixth commandment is, Thou shalt not kill." },
                new WestminsterCatechism {Number=55, Question="What is forbidden in the third commandment?", Answer="The third commandment forbiddeth all profaning or abusing of anything whereby God maketh himself known." },
                new WestminsterCatechism {Number=73, Question="Which is the eighth commandment?", Answer="The eighth commandment is, Thou shalt not steal." },
                new WestminsterCatechism {Number=39, Question="What is the duty which God requireth of man?", Answer="The duty which God requireth of man is obedience to his revealed will." },
                new WestminsterCatechism {Number=11, Question="What are God's works of providence?", Answer="God's works of providence are his most holy, wise and powerful preserving and governing all his creatures, and all their actions." },
                new WestminsterCatechism {Number=57, Question="Which is the fourth commandment?", Answer="The fourth commandment is, Remember the sabbath day, to keep it holy. Six days shalt thou labor, and do all thy work: but the seventh day is the sabbath of the Lord thy God: in it thou shalt not do any work, thou, nor thy son, nor thy daughter, thy manservant, nor thy maidservant, nor thy cattle, nor thy stranger that is within thy gates: for in six days the Lord made heaven and earth, the sea, and all that in them is, and rested the seventh day: wherefore the Lord blessed the sabbath day, and hallowed it." },
                new WestminsterCatechism {Number=59, Question="Which day of the seven hath God appointed to be the weekly sabbath?", Answer="From the beginning of the world to the resurrection of Christ, God appointed the seventh day of the week to be the weekly sabbath; and the first day of the week ever since, to continue to the end of the world, which is the Christian sabbath." },
                new WestminsterCatechism {Number=30, Question="How doth the Spirit apply to us the redemption purchased by Christ?", Answer="The Spirit applieth to us the redemption purchased by Christ, by working faith in us, and thereby uniting us to Christ in our effectual calling." },
                new WestminsterCatechism {Number=77, Question="What is required in the ninth commandment?", Answer="The ninth commandment requireth the maintaining and promoting of truth between man and man, and of our own and our neighbor's good name, especially in witness-bearing." },
                new WestminsterCatechism {Number=7, Question="What are the decrees of God?", Answer="The decrees of God are his eternal purpose, according to the counsel of his will, whereby, for his own glory, he hath foreordained whatsoever comes to pass." },
                new WestminsterCatechism {Number=94, Question="What is baptism?", Answer="Baptism is a sacrament, wherein the washing with water in the name of the Father, and of the Son, and of the Holy Ghost, doth signify and seal our ingrafting into Christ, and partaking of the benefits of the covenant of grace, and our engagement to be the Lord's." },
                new WestminsterCatechism {Number=103, Question="What do we pray for in the third petition?", Answer="In the third petition, which is, Thy will be done in earth, as it is in heaven, we pray that God, by his grace, would make us able and willing to know, obey and submit to his will in all things, as the angels do in heaven." },
                new WestminsterCatechism {Number=3, Question="What do the scriptures principally teach?", Answer="The scriptures principally teach what man is to believe concerning God, and what duty God requires of man." },
                new WestminsterCatechism {Number=8, Question="How doth God execute his decrees?", Answer="God executeth his decrees in the works of creation and providence." },
                new WestminsterCatechism {Number=64, Question="What is required in the fifth commandment?", Answer="The fifth commandment requireth the preserving the honor, and performing the duties, belonging to every one in their several places and relations, as superiors, inferiors or equals." },
                new WestminsterCatechism {Number=27, Question="Wherein did Christ's humiliation consist?", Answer="Christ's humiliation consisted in his being born, and that in a low condition, made under the law, undergoing the miseries of this life, the wrath of God, and the cursed death of the cross; in being buried, and continuing under the power of death for a time." },
                new WestminsterCatechism {Number=33, Question="What is justification?", Answer="Justification is an act of God's free grace, wherein he pardoneth all our sins, and accepteth us as righteous in his sight, only for the righteousness of Christ imputed to us, and received by faith alone." },
                new WestminsterCatechism {Number=83, Question="Are all transgressions of the law equally heinous?", Answer="Some sins in themselves, and by reason of several aggravations, are more heinous in the sight of God than others." },
                new WestminsterCatechism {Number=31, Question="What is effectual calling?", Answer="Effectual calling is the work of God's Spirit, whereby, convincing us of our sin and misery, enlightening our minds in the knowledge of Christ, and renewing our wills, he doth persuade and enable us to embrace Jesus Christ, freely offered to us in the gospel." },
                new WestminsterCatechism {Number=90, Question="How is the word to be read and heard, that it may become effectual to salvation?", Answer="That the word may become effectual to salvation, we must attend thereunto with diligence, preparation and prayer; receive it with faith and love, lay it up in our hearts, and practice it in our lives." },
                new WestminsterCatechism {Number=38, Question="What benefits do believers receive from Christ at the resurrection?", Answer="At the resurrection, believers being raised up in glory, shall be openly acknowledged and acquitted in the day of judgment, and made perfectly blessed in the full enjoying of God to all eternity." },
                new WestminsterCatechism {Number=89, Question="How is the word made effectual to salvation?", Answer="The Spirit of God maketh the reading, but especially the preaching, of the word, an effectual means of convincing and converting sinners, and of building them up in holiness and comfort, through faith, unto salvation." },
                new WestminsterCatechism {Number=106, Question="What do we pray for in the sixth petition?", Answer="In the sixth petition, which is, And lead us not into temptation, but deliver us from evil, we pray that God would either keep us from being tempted to sin, or support and deliver us when we are tempted." },
                new WestminsterCatechism {Number=25, Question="How doth Christ execute the office of a priest?", Answer="Christ executeth the office of a priest, in his once offering up of himself a sacrifice to satisfy divine justice, and reconcile us to God; and in making continual intercession for us." },
                new WestminsterCatechism {Number=29, Question="How are we made partakers of the redemption purchased by Christ?", Answer="We are made partakers of the redemption purchased by Christ, by the effectual application of it to us by his Holy Spirit." },
                new WestminsterCatechism {Number=81, Question="What is forbidden in the tenth commandment?", Answer="The tenth commandment forbiddeth all discontentment with our own estate, envying or grieving at the good of our neighbor, and all inordinate motions and affections to anything that is his." },
                new WestminsterCatechism {Number=102, Question="What do we pray for in the second petition?", Answer="In the second petition, which is, Thy kingdom come, we pray that Satan's kingdom may be destroyed; and that the kingdom of grace may be advanced, ourselves and others brought into it, and kept in it; and that the kingdom of glory may be hastened." },
                new WestminsterCatechism {Number=97, Question="What is required to the worthy receiving of the Lord's supper?", Answer="It is required of them that would worthily partake of the Lord's supper, that they examine themselves of their knowledge to discern the Lord's body, of their faith to feed upon him, of their repentance, love, and new obedience; lest, coming unworthily, they eat and drink judgment to themselves." }
            };
            wmCatechisms.ForEach(c => context.WestminsterCatechisms.Add(c));
            context.SaveChanges();
        }
    }
}